import 'package:flutter/material.dart';void main() {  runApp(MaterialApp(    home: Scaffold(      backgroundColor: Colors.red,      appBar: AppBar(        title: const Text('Dicee'),        backgroundColor: Colors.red,      ),      body: const DicePage(),    ),  ));}class DicePage extends StatefulWidget {  const DicePage({super.key});  @override  _DicePageState createState() => _DicePageState();}class _DicePageState extends State<DicePage> {  int leftDiceNumber = 1;  int rightDiceNumber = 1;  @override  Widget build(BuildContext context) {    return Center(      child: Row(        mainAxisAlignment: MainAxisAlignment.center,        crossAxisAlignment: CrossAxisAlignment.center,        children: [          Expanded(            child: TextButton(                onPressed: () {                  setState(() {                    leftDiceNumber = 2;                    print('Left button pressed');                  });                },                child: Image(                    image: AssetImage('images/dice$leftDiceNumber.png')                )            ),          ),          Expanded(            child: TextButton(                onPressed: () {                  setState(() {                    rightDiceNumber = 3;                  });                },                child: Image(                    image: AssetImage('images/dice$rightDiceNumber.png')                )            ),          ),        ],      ),    );  }}